sys_variable_value.value:
(function execute(inputs, outputs) {

  var first_glacier = parseInt(inputs.first_glacier)
  var glaciers_per_page = parseInt(inputs.glaciers_per_page)
  var pages_to_get = parseInt(inputs.pages_to_get)

  if(first_glacier <= 0 || first_glacier > 132890){
    outputs.first_glacier_to_get = "1";
  } else {
    outputs.first_glacier_to_get = inputs.first_glacier;
  }

  if(glaciers_per_page <= 0 || glaciers_per_page > 10){
    outputs.glaciers_per_page = "1";
  } else {
    outputs.glaciers_per_page = inputs.glaciers_per_page;
  }

  if(pages_to_get <= 0){
    outputs.number_of_pages = "1";
  } else {
    outputs.number_of_pages = inputs.pages_to_get;
  }

})(inputs, outputs);

